import logo from './logo.svg';
import './App.css';
import { Fragment, useMemo, useState, ul } from 'react';

function Smth1(props){
  return <p>{props.cos}</p>;

}

function Smth2(props){
  return <p>{props.children}</p>;

}
function WlasnaLista({lista, wrapper = ul, item}){
  return(
    <wrapper>
      {lista.map(item)}
    </wrapper>
    // <ul>
    // {lista.map(it => <li>{it}</li>)}
    // </ul>
  );
}

function ElementListy({index, dane, updateList}){
  const [show, setShow] = useState(false);
  const onUpdateClick = state => updateList(
    list => {
      let copy = [...list]
      copy[index] = state;
      return copy;
    }
  );

  return(
    <li>
      <h4 onClick={() => setShow(it => !it)}>{dane}</h4>
      {show ? <SimpleForm initialData={dane} onButtonClick={onUpdateClick} buttonName="Zmien"/> : <></>}

    </li>
  );
}

function SimpleForm({ initialData, onButtonClick, buttonName}){
  const [state, setState] = useState(initialData);

  return(
    <>
      <input type="number" value={state} onChange={e => setState(e.target.value)}/>
      <button onClick={() => onButtonClick(state)}>{buttonName}</button>
    </>
  )
}

function Example(){
  const [number, setNumber] = useState(0);

  return (
    <div>
      <button onClick={() => setNumber(n => n+1)}>+</button>
      <input type="number" value={number} onChange={e=>setNumber(e.target)}/>
      <button onClick={() => setNumber(n => n-1)}>-</button>
      {number <0 ? <h1>Mniejsza od 0</h1> : <></>}
    </div>
  );
}

